  const percentage = total > 0 ? (count / total) * 100 : NaN;
  const percentageColor = color(percentage);
  const percentageStyle = { backgroundColor: percentageColor };

  return count > 0 ? (
    <div style={percentageStyle} className="progress-bar">
      {percentage.toFixed(2)}%
    </div>
  ) : (
    <p className="text-muted">No data yet</p>
  );
};

