    labels: categories,
    datasets: [
      {
        data: tweets.map(tweet => tweet.count),
        color: (values) => {
          const total = values.reduce((a, b) => a + b, 0);
          const percentage = total > 0 ? (values.map(value => value / total) * 100) : NaN;
          const percentageColor = d3.scaleOrdinal(d3.schemeCategory10).range(["#1f77b4", "#ff7f65"]);
          return d3.scaleOrdinal()
            .domain(percentage)
            .range([d3.rgb(d3.color("white")), d3.rgb(d3.color("black"))]);
        },
      },
    ],
  };

  return (
    <div className="card">
      <div className="card-header">
        <div className="row">
          <div className="col">Tweet Analysis</div>
          <div className="col">{categories.length} categories</div>
        </div>
      </div>
      <div className="progress-bar" style={{ height: '10px' }}>
        {progressBarChart}
      </div>
      {categories.map((category, index) => (
        <div key={category} className="card-body">
          <h4 className="card-title">{category}</h4>
          <div className="progress-bar" style={{ height: '10px' }}>
            {progressBarChart}
          </div>
        </div>
      ))}
    </div>
  );
};

