import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProgressBar = ({ count, total, color }) => {
  const percentage = total > 0 ? (count / total) * 100 : NaN;
  const percentageColor = color(percentage);
  const percentageStyle = { backgroundColor: percentageColor };

  return count > 0 ? (
    <div style={percentageStyle} className="progress-bar">
      {percentage.toFixed(2)}%
    </div>
  ) : (
    <p className="text-muted">No data yet</p>
  );
};

const TweetAnalysis = ({ tweets, categories }) => {
  const progressBarChart = {
    labels: categories,
    datasets: [
      {
        data: tweets.map(tweet => tweet.count),
        color: (values) => {
          const total = values.reduce((a, b) => a + b, 0);
          const percentage = total > 0 ? (values.map(value => value / total) * 100) : NaN;
          const percentageColor = d3.scaleOrdinal(d3.schemeCategory10).range(["#1f77b4", "#ff7f65"]);
          return d3.scaleOrdinal()
            .domain(percentage)
            .range([d3.rgb(d3.color("white")), d3.rgb(d3.color("black"))]);
        },
      },
    ],
  };

  return (
    <div className="card">
      <div className="card-header">
        <div className="row">
          <div className="col">Tweet Analysis</div>
          <div className="col">{categories.length} categories</div>
        </div>
      </div>
      <div className="progress-bar" style={{ height: '10px' }}>
