          <div className="progress-bar" style={{ height: '10px' }}>
            {progressBarChart}
          </div>
        </div>
      ))}
    </div>
  );
};

const TweetAnalysisApp = () => {
  const [tweets, setTweets] = useState([]);
  const [categories, setCategories] = useState([]);

